export default {
  name: "EfDrawer",
  props: {
    show: {
      require: false,
      type: Boolean,
      default: false
    },
    title: {
      require: false,
      type: String,
      default: "提示"
    },
    view: {
      require: false,
      type: String,
      default: ""
    },
    width: {
      require: false,
      type: [String, Number],
      default: 600
    },
    params: {
      require: false,
      type: Object,
      default: function _default() {
        return {};
      }
    }
  },
  computed: {
    dialogWidth: function dialogWidth() {
      var width = "50%";

      if (this.widthInner) {
        if (typeof this.widthInner == "number") {
          width = this.widthInner + "px";
        } else {
          width = this.widthInner;
        }
      }

      return width;
    }
  },
  data: function data() {
    return {
      componentType: 'EfDrawer',
      visible: false,
      tempComponent: null,
      maskClose: false,
      body: true,
      // 新增代码
      showInner: this.show,
      titleInner: this.title,
      viewInner: this.view,
      widthInner: this.width,
      paramsInner: this.params
    };
  },
  watch: {
    show: function show(newl) {
      this.visible = newl;
      this.fullscreen = false;
    }
  },
  created: function created() {},
  methods: {
    close: function close(bakParams) {
      if (typeof bakParams == 'undefined') {
        bakParams = null;
      }

      this.visible = false;

      if (typeof this.callBackFun == 'function') {
        this.callBackFun(bakParams);
      }
    },
    getComponent: function getComponent() {
      var _this = this;

      if (this.viewInner) {
        this.tempComponent = function (resolve) {
          return require(["@/views".concat(_this.viewInner)], resolve);
        };
      }
    },
    handleClose: function handleClose() {
      this.close(null);
    },
    showDrawer: function showDrawer(params) {
      if (typeof params == 'undefined' || params == null || params == "") {
        alert("slideReveal函数错误，params不可为空");
        return;
      }

      var title = params.title;
      var view = params.view;
      var width = params.width;
      var callBackFun = params.callBackFun;
      var iframeFlag = params.iframeFlag; //打开模式   默认:angularjs 路由   iframe时view嵌入iframe中展示

      var windowParams = params.params;

      if (typeof windowParams == 'undefined') {
        windowParams = {};
      }

      if (iframeFlag != true) {
        iframeFlag = false;
      }

      if (typeof view == 'undefined' || view == null || view == "") {
        alert("slideReveal函数错误，view不可为空");
        return;
      }

      if (typeof title == 'undefined' || title == null || title == "") {
        title = "";
      }

      if (typeof width == 'undefined' || width == null) {
        width = 500;
      }

      this.visible = true;
      this.fullscreen = false;
      this.titleInner = title;
      this.viewInner = view;
      this.widthInner = width;
      this.paramsInner = windowParams;
      this.callBackFun = callBackFun;

      if (this.viewInner) {
        var path = this.viewInner; // path="@/page/frame/systemmanagement/usermanagement/user/usermanagement"
        // console.log(path);

        path = path.replace(/\/\//g, "/");

        if (path.indexOf('/') == 0) {
          path = path.substring(1, path.length);
        } else if (path.indexOf('@/') == 0) {
          path = path.substring(2, path.length);
        } // console.log(path);


        this.tempComponent = function (resolve) {
          return require(["@/".concat(path, ".vue")], resolve);
        };
      }
    },
    back: function back() {
      this.close();
    }
  }
};
