import { BaseFieldTag } from "./BaseFieldTag";
export default {
  name: "EfMail",
  mixins: [BaseFieldTag],
  //继承基础BaseFieldTag
  props: {
    placeholder: {
      // 选择框提示文字，默认‘请选择’，非必传。
      type: String,
      default: "格式:mail@mail.com"
    }
  },
  data: function data() {
    return {
      componentType: 'EfMail',
      //设置默认的邮箱校验方式
      compPattern: /^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+((.[a-zA-Z0-9_-]{2,3}){1,2})$/
    };
  },
  watch: {
    value: {
      //深度监听，可监听到对象、数组的变化
      immediate: true,
      handler: function handler(val) {
        this.compValue = val;
      } // deep: true //true 深度监听

    }
  },
  computed: {
    formStyle: function formStyle() {
      return this.$store.state.common.formStyle;
    },
    placeholderClass: function placeholderClass() {
      return this.$store.state.common.formStyle[0].size;
    }
  },
  methods: {
    input: function input(val) {
      this.$emit('compChange', val);
      this.$emit('onInput', val);
    }
  }
};
