export default {
  name: 'basicBlock',
  props: {
    icon: {
      type: String,
    },
    background: {
      type: String,
    },
    to: {
      type: Object,
      default: () => {
        return {}
      }
    },
    text: {
      type: String,
    },
    dept: {
      type: String,
    },
    time: {
      type: [Number, String]
    },
    gutter: {
      type: [Number, String],
      default: 5,
    },
    color: {
      type: String,
    },
    width: {
      type: [Number, String]
    },
    height: {
      type: [Number, String]
    }
  },
  computed: {
    styleName () {
      return {
        animationDelay: `${this.time / 25}s`,
        width: this.setPx(this.width),
        height: this.setPx(this.height),
        margin: this.setPx(this.gutter)
      }
    },
    boxStyleName () {
      return {
        backgroundColor: this.color,
        backgroundImage: `url('${this.background}')`,

      }

    }
  }
}
