import {mapGetters} from "vuex";
import tags from "./tags";
import screenshot from './screenshot';
import search from "./search";
import logo from "./logo";
import top from "./top/";
import sidebar from "./sidebar/";
import {validatenull} from "@/util/validate";
import EfDialog from "@/components/ef-element-comps/EfDialog";
import EfDrawer from "@/components/ef-element-comps/EfDrawer";


export default {
  components: {
    top,
    logo,
    tags,
    search,
    sidebar,
    // setting,
    screenshot,
    EfDialog,
    EfDrawer,
  },
  name: "indexT1",
  provide() {
    return {
      index: this
    };
  },
  data() {
    return {
      //搜索控制
      isSearch: false,
    };
  },
  created() {

  },
  mounted() {

  },
  computed: mapGetters(["isHorizontal", "setting", "isRefresh", "isLock", "isCollapse", "website", "menu", "userInfo"]),
  props: [],
  methods: {
    //打开菜单
    openMenu(item = {}) {
      this.$store.dispatch("GetMenu", item.parentId).then(data => {
        if (data.length !== 0) {
          this.$router.$avueRouter.formatRoutes(data, true);
        }
        //当点击顶部菜单做的事件
        if (!validatenull(item)) {
          let itemActive = {},
              childItemActive = 0;
          //vue-router路由
          if (item.path) {
            itemActive = item;
          } else {
            if (this.menu[childItemActive].length == 0) {
              itemActive = this.menu[childItemActive];
            } else {
              itemActive = this.menu[childItemActive].children[childItemActive];
            }
          }
          this.$store.commit('SET_MENUID', item);
          this.$router.push({
            path: this.$router.$avueRouter.getPath({
              name: itemActive.label,
              src: itemActive.path
            }, itemActive.meta)
          });
        }
      });
    },

  }
};
