<template>
     <div class="example">
        <div>
            <dl>
                <dt>
                    Animate:
                </dt>
                <dd>
                     <button class="k-button" @click="animate">Animate</button>
                </dd>
            </dl>
            <Animation :appear="show"
                    :transition-name="'custom-animation'"
                    :component-child-class-name="'child'">
                <div class="content">CONTENT</div>
            </Animation>
        </div>
    </div>
</template>
<script>
import './styles.css';
import { Animation } from '@progress/kendo-vue-animation';

export default {
    components: {
      'Animation': Animation
    },
     data: function () {
        return {
             show: true
        };
    },
    methods:{
        animate(){
             this.show = !this.show;
        }
    }
}
</script>
