<template>
  <div class="example">
        <div>
            <dl>
                <dt>
                    Slide:
                </dt>
                <dd>
                     <button class="k-button" @click="animate">Animate</button>
                </dd>
            </dl>
            <Slide :appear="show">
                <div class="content">CONTENT</div>
            </Slide>
        </div>
    </div>
</template>
<script>
import './styles.css';
import { Slide } from '@progress/kendo-vue-animation';

export default {
    components: {
      'Slide': Slide
    },
    data: function () {
        return {
             show: false
        };
    },
    methods:{
        animate(){
             this.show = !this.show;
        }
    }
}
</script>
