<template>
   <div class="example">
        <div>
            <dl>
                <dd>
                    <dt>
                        Color:
                    </dt>
                    <dd>
                        <select name="color" v-model="color">
                            <option value="red">red</option>
                            <option value="green">green</option>
                            <option value="blue">blue</option>
                        </select>
                    </dd>
                </dd>
                <dt>
                    Slide:
                </dt>
                <dd>
                     <button class="k-button" @click="animate">Animate</button>
                </dd>
            </dl>
            <Slide :appear="show" class="wrapper" :style="{ color: color, border: `1px solid ${color}` }">
                <div class="content">CONTENT</div>
            </Slide>
        </div>
    </div>
</template>
<script>
import './styles.css';
import { Slide } from '@progress/kendo-vue-animation';

export default {
    components: {
      'Slide': Slide
    },
    data: function () {
        return {
             show: false,
             color: 'red'
        };
    },
    methods:{
        animate(){
             this.show = !this.show;
        }
    }
}
</script>
