<template>
  <div class="example">
        <div>
            <dl>
             <dt>
                <dl>
                    <input
                            type="checkbox"
                            id="enter"
                            class="k-checkbox"
                            name="enter"
                            v-model="enter"
                        />
                        <label for="enter" class="k-checkbox-label">
                            Enter:&nbsp;
                        </label>
                        <br />
                        <input
                            type="checkbox"
                            id="exit"
                            class="k-checkbox"
                            name="exit"
                            v-model="exit"
                        />
                        <label for="exit" class="k-checkbox-label">
                            Exit:&nbsp;
                        </label>
                    </dl>
                </dt>
                <dt>
                    Slide:
                </dt>
                <dd>
                     <button class="k-button" @click="animate">Animate</button>
                </dd>
            </dl>
             <Slide :appear="show" :enter="enter" :exit="exit">
                         <div class="content">
                            CONTENT
                            </div >
                    </Slide>
        </div>
    </div>
</template>
<script>
import './styles.css';
import { Slide } from '@progress/kendo-vue-animation';

export default {
    components: {
      'Slide': Slide
    },
    data: function () {
        return {
             show: false,
             enter: true,
             exit: true
        };
    },
    methods:{
        animate(){
             this.show = !this.show;
        }
    }
}
</script>
