import Vue from 'vue';
import { Expand, ExpandDirection } from '../src/main';

interface AppState {
    expand: boolean;
    direction: ExpandDirection;
}

Vue.component('Expand', Expand);
let a = new Vue({
    el: '#app',
    data: function () {
        return {
            expand: true,
            direction: 'vertical'
        };
    },
    methods: {
        onClick: function() {
            this.expand = !this.expand;
        },
        onDirectionChange: function(e: any) {
            /* eslint-disable no-console */
            this.direction = e.target.value;
        }
    }
});
