import Vue from 'vue';
import { Fade } from '../src/main';

Vue.component('fade', Fade);
let a = new Vue({
    el: '#app',
    data: function () {
        return {
            index: 1,
            key: new Date().getTime()
        };
    },
    methods: {
        onClick: function() {
            this.key = new Date().getTime();
            this.index = this.index + 1;
        }
    }
});
