import Vue from 'vue';
import { Reveal } from '../src/main';

Vue.component('Reveal', Reveal);
let a = new Vue({
    el: '#app',
    data: function () {
        return {
            direction: 'vertical',
            expand: true
        };
    },
    methods: {
        onClick: function() {
            this.expand = !this.expand;
        }
    }
});
