import Vue from 'vue';
import { Zoom } from '../src/main';

Vue.component('Zoom', Zoom);
let a = new Vue({
    el: '#app',
    data: function () {
        return {
            direction: 'in',
            index: 1
        };
    },
    methods: {
        onClick: function() {
            this.index = this.index + 1;
        }
    }
});
