// @ts-ignore
import * as Vue from 'vue';
const allVue = Vue as any;
const gh = allVue.h;
import { RecordPropsDefinition, ComponentOptions } from 'vue/types/options';
type DefaultData<V> =  object | ((this: V) => {});
type DefaultMethods<V> =  { [key: string]: (this: V, ...args: any[]) => any };
import { Animation } from './Animation';
import { AnimationInterface } from './AnimationInterface';
import { VNode } from 'vue';
import { getDefaultSlots } from '@progress/kendo-vue-common';

// tslint:disable:max-line-length
/**
 * Represent the props of the [KendoVue Fade Animation component]({% slug animationtypes_animation_native %}#toc-fade).
 *
 * @example
 * ```html-preview
 * 
 * ```
 */
export interface FadeProps extends AnimationInterface {
    /**
     * After the element reaches its exit state, it is no longer available
     * in the DOM. If a DOM operation is required, access it trough the `childFactory` function.
     */
    childFactory?: any;
    /**
     * Specifies CSS class names which are set to the Animation.
     */
    className?: string;
    /**
     * Specifies the node type of the parent Animation. Defaults to `div`.
     */
    component?: string;
    /**
     * Specifies the id of the Animation.
     */
    id?: string;

    /**
     * Specifies the tag.
     */
    tag?: String;
}
/**
 * Represents the default `Fade` component.
 */
let Fade: ComponentOptions<Vue, DefaultData<{}>, 
DefaultMethods<{}>, {}, RecordPropsDefinition<FadeProps>> = {
    props: {
        appear: {
            type: Boolean,
            default: false
        },
        enter: {
            type: Boolean,
            default: true
        },
        exit: {
            type: Boolean,
            default: false
        },
        transitionEnterDuration: {
            type: Number,
            default: 500
        },
        transitionExitDuration: {
            type: Number,
            default: 500
        },
        childFactory: Object,
        className: String,
        tag: String,
        id: String
    },
    // @ts-ignore
    setup: (!gh) ? undefined : function() {
        const v3 = !!gh;
        return {
            v3
        };
    },
    // @ts-ignore
    render(this: Vue, createElement: any): VNode {
        const h = gh || createElement;
        const defaultSlot = getDefaultSlots(this);
        return (
         // @ts-ignore function children
        <Animation 
                transitionName= {`fade`}
                appear = { this.$props.appear }
                enter = { this.$props.enter }
                exit = { this.$props.exit }
                transitionEnterDuration = { this.$props.transitionEnterDuration }
                transitionExitDuration = { this.$props.transitionExitDuration } >
                {defaultSlot}
        </Animation>
        );
    }
};

export { Fade };
