/**
 * @hidden
 */
export const focusFirstFocusableChild = (element: any): void => {
    if (element) {
        // should focus wrapper if it's focusable in future versions
        const elements = element.querySelectorAll('input, [tabindex]:not([tabindex="-1"])');
        if (elements.length && (elements[0] as any).focus) {
            (elements[0] as any).focus();
        }
    }
};
