/**
 * @hidden
 */
const DISABLED_TABINDEX = -1;
/**
 * @hidden
 */
const DEFAULT_TABINDEX = 0;

/**
 * @hidden
 */
export const getTabIndex = (
    tabIndex?: number | string,
    disabled?: boolean,
    useDefaultTabIndexWhenDisabled?: boolean
): number | undefined => {
    let parsedTabIndex = typeof tabIndex === 'string' ? parseInt(tabIndex, undefined) : tabIndex;

    if (parsedTabIndex === NaN) {
        return undefined;
    }

    return parsedTabIndex !== undefined
    ? parsedTabIndex
        : disabled ?
            (useDefaultTabIndexWhenDisabled ? undefined : DISABLED_TABINDEX)
            : DEFAULT_TABINDEX;
};
