/**
 * @hidden
 */
export function hasListener(this: any, eventName: string) {
    if (this.v3) {
     return Object.keys(this.$attrs).map(name => name.toLowerCase())
     .some(event => event.endsWith(eventName.toLowerCase()));
   } else {
      return this.$listeners[eventName];
    }
 }

/**
 * @hidden
 */
export function getListeners(this: any) {
    if (this.v3) {
        let listeners = {};
        for (let key in (this.$attrs)) {
            if (key.startsWith('on')) {
                listeners[key] = this.$attrs[key];
            }
        }

        return listeners;
    } else {
        return this.$listeners;
    }
}
