<template>
   <div>
    <div class="example-config row">
                        <div class="col-sm-4 col-xs-12">
                            <h4>Anchor align point</h4>
                            <p>
                                Horizontal <br />
                                <select
                                    v-model="anchorHorizontal"
                                    class="k-textbox"
                                >
                                    <option value="left">Left</option>
                                    <option value="center">Center</option>
                                    <option value="right">Right</option>
                                </select>
                            </p>
                            <p>
                                Vertical <br />
                                <select
                                    v-model="anchorVertical"
                                    class="k-textbox"
                                >
                                    <option value="top">Top</option>
                                    <option value="middle">Middle</option>
                                    <option value="bottom">Bottom</option>
                                </select>
                            </p>
                        </div>
                        <div class="col-sm-4 col-xs-12">
                            <h4>Popup align point</h4>
                            <p>
                                Horizontal <br />
                                <select
                                    v-model="popupHorizontal"
                                    class="k-textbox"
                                >
                                    <option value="left">Left</option>
                                    <option value="center">Center</option>
                                    <option value="right">Right</option>
                                </select>
                            </p>
                            <p>
                                Vertical <br />
                                <select
                                    v-model="popupVertical"
                                    class="k-textbox"
                                >
                                    <option value="top">Top</option>
                                    <option value="middle">Middle</option>
                                    <option value="bottom">Bottom</option>
                                </select>
                            </p>
                        </div>
                        <div class="col-sm-4 col-xs-12">
                            <h4>Popup action</h4>
                            <br />
                               <button
                                class="k-button"
                                @click="onClick"
                            >
                                {{buttonText}}
                            </button>
                        </div>
                    </div>
        <span
            ref="anchor"
            class="anchor content"
        >
            ANCHOR
        </span>
     
        <Popup
            :anchor="'anchor'"
            :show="show"
            :anchor-align="anchorAlign"
            :popup-align="popupAlign"
        >
           <div class="content popup-inner-content">
                <p>Popup content.</p>
            </div>
        </Popup>
    </div>
</template>
<script>
import './styles.css';
import { Popup } from '@progress/kendo-vue-popup';

export default {
    components: {
      'Popup': Popup
    },
     data: function(){
        return {
            anchor: null,
            anchorHorizontal: 'left',
            anchorVertical: 'bottom',
            popupHorizontal: 'right',
            popupVertical: 'top',
            show: false
        };
    },
    computed: {
        buttonText: function(){
            return this.show ? 'Hide' : 'Show';
        },
        anchorAlign: function(){
            return {
                horizontal: this.anchorHorizontal,
                vertical: this.anchorVertical
            };
        },
        popupAlign: function(){
            return {
                horizontal: this.popupHorizontal,
                vertical: this.popupVertical
            };
        }
    },
    mounted(){
        this.show= true;
    },
    methods: {
         onClick () {
            this.show = !this.show;
        }
    }
};

</script>
