<template>
 <div>
       <button
            class="k-button"
            @click="onClick"
            ref="button"
        >
            {{buttonText}}
        </button>
        <Popup
            :anchor="'button'"
            :show="show"
            :popup-class="'popup-content'"
        >
            Popup content.
        </Popup>
    </div>
</template>
<script>
import './styles.css';
import { Popup } from '@progress/kendo-vue-popup';

export default {
    components: {
      'Popup': Popup
    },
    data: function(){
        return {
            show: false
        };
    },
    computed: {
        buttonText: function(){
            return this.show ? 'Hide' : 'Show';
        }
    },
    mounted(){
        this.show= true;
    },
    methods: {
         onClick () {
            this.show = !this.show;
        }
    }
};

</script>
