import Vue from 'vue';

import { Popup } from '../src/Popup';

Vue.component(Popup.name, Popup);
// @ts-ignore
let a = new Vue({
    el: '#app',
    data: function () {
        return {
            show: false,
            showInner: false
        };
    },
    methods: {
        togglePopup() {
            this.show = !this.show;
            this.showInner = this.show ? false : this.showInner;
        },
        toggleInnerPopup() {
            this.showInner = !this.showInner;
        }
    }
});
